import React, {Component} from 'react';
import {connect} from 'react-redux'
import * as R from "ramda";


import {fetchPhoneById} from "actions";
import {getPhoneById} from "selectors"

class Phone extends Component {

    componentDidMount() {
        this.props.fetchPhoneById(this.props.match.params.id)
    }

    render() {
        console.log('phone', this.props.phone)
        const {phone} = this.props
        return (
            <div className='view-container'>
                <div className="container">
                    <div className="row">
                        <div className="col-md-9">
                            {phone && this.renderContent()}
                        </div>
                        <div className="col-md-3">
                            {phone && this.renderSidebar()}
                        </div>
                    </div>
                </div>
            </div>

        )
    }

    renderContent() {
        const {phone} = this.props

        return (<div className='thumbnail'>
                <div className='row'>
                    <div className='col-md-6'>
                        <img className="img-thumbnail"
                             src={phone.image}
                             alt={phone.name}
                        /></div>
                    <div className='col-md-6'>
                        {this.renderFields()}
                    </div>
                </div>
                <div className='caption-full'>
                    <h4 className='pull-right'> ${phone.price}</h4>
                    <h4 className='pull-right'> ${phone.name}</h4>
                    <p>{phone.description}</p>

                </div>

            </div>
        )
    }

    renderSidebar() {
        return (
            <div></div>

        )
    }

    renderFields() {
        const {phone} = this.props
        const columnFields = R.compose(
            R.toPairs,
            R.pick(['cpu', 'camera', 'size', 'weight', 'display', 'battery', 'memory'])
        )(phone)
        return (
            columnFields.map(([k, v]) => (

                <div className='column' key={k}>
                    <div className='ab-details-title'>
                        <p>{k}</p>
                    </div>
                    <div className='ab-details-info'>
                        {v}
                    </div>
                </div>

            ))
        )
    }
}

const mapDispachToProps = {
    fetchPhoneById
}

const mapStateToProps = state => {
    return {
        phone: getPhoneById(state, state.phonePage.id)
    }
}

export default connect(mapStateToProps, mapDispachToProps)(Phone)